mapper.update:
UPDATE MP_BOARD
SET TITLE =
#{title},
CONTENT = #{content}
WHERE BNO = #{bno}

UPDATE USERS SET
ISREPORT = 'true'
WHERE ID = #{id}

UPDATE MP_BOARD SET
CNT = CNT+1
WHERE BNO = #{bno}

UPDATE MP_BOARD SET
RECOMMEND = RECOMMEND+1
WHERE BNO = #{bno}

UPDATE MP_BOARD B
SET B.REPLY_COUNT = (SELECT COUNT(RNO) FROM MP_REPLY WHERE BNO = #{bno})
WHERE B.BNO = #{bno}
