mapper.update:
UPDATE QNA_BOARD
SET TITLE =
#{title},
CONTENT = #{content}
WHERE BNO = #{bno}

UPDATE USERS SET
ISREPORT = 'true'
WHERE ID = #{id}

UPDATE QNA_BOARD SET
CNT = CNT+1
WHERE BNO = #{bno}

UPDATE QNA_BOARD SET
RECOMMEND = RECOMMEND+1
WHERE BNO = #{bno}

UPDATE QNA_BOARD QNA
SET QNA.REPLY_COUNT = (SELECT COUNT(RNO) FROM QNA_REPLY WHERE BNO = #{bno})
WHERE QNA.BNO = #{bno}
