mapper.update:
UPDATE USERS SET
ISREPORT = 'true'
WHERE ID = #{id}

UPDATE USERS SET
PNO = #{pno}
WHERE ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM USERS
WHERE PNO = #{pno}

mapper.update:
UPDATE USERS SET 
PNO = 0 
WHERE ID = #{id}

UPDATE TEAM_BOARD SET
CNT = CNT+1
WHERE BNO = #{bno}

UPDATE TEAM_BOARD SET
COMPLETE = 'complete'
WHERE BNO = #{bno}

UPDATE USERS SET
PNO = 0
WHERE PNO = #{pno}

UPDATE TEAM_BOARD SET
RECOMMEND = RECOMMEND+1
WHERE BNO = #{bno}

UPDATE TEAM_BOARD TEAM
SET TEAM.REPLY_COUNT = (SELECT COUNT(RNO) FROM TEAM_REPLY WHERE BNO = #{bno})
WHERE TEAM.BNO = #{bno}
