mapper.insert:

 	SELECT IT_BOARD_SEQ.NEXTVAL FROM DUAL

 INSERT INTO IT_BOARD(    BNO 
					       , TITLE 
					       , CONTENT 
					       , WRITER  )
               VALUES(    #{bno}
              		   , #{title}
              		   , #{content}
              		   , #{writer}  )

mapper.update:
UPDATE IT_BOARD
SET TITLE =
#{title},
CONTENT = #{content}
WHERE BNO = #{bno}

UPDATE USERS SET
ISREPORT = 'true'
WHERE ID = #{id}

UPDATE IT_BOARD SET
CNT = CNT+1
WHERE BNO = #{bno}

UPDATE IT_BOARD SET
RECOMMEND = RECOMMEND+1
WHERE BNO = #{bno}

UPDATE IT_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

UPDATE IT_BOARD IT
SET IT.REPLY_COUNT = (SELECT COUNT(RNO) FROM IT_REPLY WHERE BNO = #{bno})
WHERE IT.BNO = #{bno}
