mapper.update:
UPDATE N_BOARD
SET TITLE =
#{title},
CONTENT = #{content}
WHERE BNO = #{bno}

UPDATE USERS SET
ISREPORT = 'true'
WHERE ID = #{id}

UPDATE N_BOARD SET
CNT = CNT+1
WHERE BNO = #{bno}

UPDATE N_BOARD SET
RECOMMEND = RECOMMEND+1
WHERE BNO = #{bno}

UPDATE N_BOARD N
SET N.REPLY_COUNT = (SELECT COUNT(RNO) FROM N_REPLY WHERE BNO = #{bno})
WHERE N.BNO = #{bno}
